import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './cart.css';

function CartPage({ cart, onRemoveFromCart }) {
    const navigate = useNavigate();
    const [errorMessage, setErrorMessage] = useState('');
    const [promoCode, setPromoCode] = useState('');
    const [isPromoValid, setIsPromoValid] = useState(null);

    // Vérifie si le panier existe
    console.log("Props reçues dans CartPage:", { cart, onRemoveFromCart });

    if (!cart) {
        console.log("Erreur: cart est undefined!");
        return <p>Erreur : Le panier ne peut pas être chargé.</p>;
    }

    console.log("Contenu du panier:", cart);

    // Codes promo valides
    const validPromoCodes = {
        "ISIRA07": 0.9, // 10% de réduction
        "FREESHIP": 1   // Prix normal
    };

    // Calcul du total avec ou sans réduction
    const calculateTotal = () => {
        console.log("=== DÉBUT CALCUL TOTAL ===");
        let total = cart.reduce((acc, item) => {
            console.log(`Calcul pour ${item.name}: ${item.quantity} x ${item.price} FCFA`);
            return acc + item.quantity * item.price;
        }, 0);

        if (isPromoValid !== null && validPromoCodes[promoCode]) {
            const reduction = (1 - validPromoCodes[promoCode]) * 100;
            console.log(`Application du code promo: -${reduction}%`);
            total *= validPromoCodes[promoCode];
        }

        console.log(`Total final calculé: ${total} FCFA`);
        console.log("=== FIN CALCUL TOTAL ===");
        return total;
    };

    // Gestion du changement de code promo
    const handlePromoChange = (e) => {
        const code = e.target.value.toUpperCase();
        console.log("\n=== VÉRIFICATION CODE PROMO ===");
        console.log("Code saisi:", code);

        setPromoCode(code);
        if (validPromoCodes[code]) {
            console.log("✓ Code promo valide");
            setIsPromoValid(true);
            setErrorMessage('');
        } else {
            console.log("✗ Code promo invalide");
            setIsPromoValid(false);
            setErrorMessage('Code promo invalide.');
        }
        console.log("=== FIN VÉRIFICATION CODE PROMO ===\n");
    };

    // Gestion du processus de livraison
    const handleProceedToDelivery = async () => {
        console.log("\n====== DÉBUT PROCÉDURE DE LIVRAISON ======");

        if (cart.length > 0) {
            const orderData = {
                cart: cart,
                totalPrice: calculateTotal(),
                appliedPromo: promoCode || null
            };

            console.log("\n=== DONNÉES COMPLÈTES DE LA COMMANDE ===");
            console.log(orderData);

            try {
                const response = await fetch('http://localhost:5000/api/checkout', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(orderData)
                });

                const data = await response.json();
                console.log("Réponse du serveur :", data);

                if (response.ok) {
                    navigate('/delivery', { state: orderData });
                    console.log("\n✓ Navigation vers la page de livraison");
                } else {
                    console.error("Erreur lors de l'envoi des données:", data);
                }
            } catch (error) {
                console.error("Erreur réseau:", error);
            }

            console.log("====== FIN PROCÉDURE DE LIVRAISON ======\n");
        } else {
            console.log("⚠ ATTENTION: Tentative de validation d'un panier vide");
            setErrorMessage('Votre panier est vide. Ajoutez des articles avant de valider la commande.');
        }
    };


    return (
        <div className="cart-page">
            <button 
                className="back-button" 
                onClick={() => {
                    console.log("Navigation: Retour à la page précédente");
                    navigate(-1);
                }}
            >
                Retour
            </button>

            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img 
                                src={`${process.env.PUBLIC_URL}${item.imgSrcs[0]}`} 
                                alt={item.name} 
                                className="cart-item-image" 
                            />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantité: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price} FCFA</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price} FCFA</span>
                            <button 
                                onClick={() => {
                                    console.log(`Suppression de l'article: ${item.name}`);
                                    onRemoveFromCart(item.id);
                                }} 
                                className="remove-button"
                            >
                                Retirer
                            </button>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>

            <div className="promo-section">
                <input 
                    type="text" 
                    placeholder="Code promo" 
                    value={promoCode} 
                    onChange={handlePromoChange} 
                    className={`promo-input ${
                        isPromoValid === true ? "valid-input" : 
                        isPromoValid === false ? "invalid-input" : ""
                    }`}
                    style={{
                        borderRadius: '8px',
                        border: '2px solid orange',
                        padding: '8px',
                        width: '200px',
                        textAlign: 'center',
                        color: isPromoValid === true ? 'green' : 
                               isPromoValid === false ? 'red' : 'black'
                    }}
                />
                {errorMessage && (
                    <p className="error-message">{errorMessage}</p>
                )}
            </div>

            <div className="cart-summary">
                <span className="total-general">
                    Total Général ({cart.length} articles) = 
                    <span className={
                        isPromoValid === true ? "valid-total" : 
                        isPromoValid === false ? "invalid-total" : ""
                    }>
                        {calculateTotal()} FCFA
                    </span>
                </span>
                <button 
                    className="validate-button" 
                    onClick={handleProceedToDelivery}
                >
                    Procéder à la livraison
                </button>
            </div>
        </div>
    );
}

export default CartPage;
