// src/App.js

import React, { useState } from 'react';
import Cart from './components/Cart';
import DeliveryModal from './components/DeliveryModal';
import Modal from './components/Modal';
import Product from './components/Product';

function App() {
    const [cart, setCart] = useState([]);
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [isDeliveryModalOpen, setIsDeliveryModalOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);

    const handleAddToCart = (product) => {
        setCart([...cart, { ...product, quantity: 1 }]);
        setSelectedProduct(null);  // Close the product modal after adding to cart
    };

    // const handleOpenCart = () => {
    //     setIsCartOpen(true);
    // };

    const handleCloseCart = () => {
        setIsCartOpen(false);
    };

    const handleOpenDeliveryModal = () => {
        setIsCartOpen(false);  // Close cart modal
        setIsDeliveryModalOpen(true);
    };

    const handleCloseDeliveryModal = () => {
        setIsDeliveryModalOpen(false);
    };

    const handleProductClick = (product) => {
        setSelectedProduct(product);
    };

    const handleCloseProductModal = () => {
        setSelectedProduct(null);
    };

    return (
        <div className="App">
            {/* Affichage des produits */}
            <div className="content">
                {/* Example of products array */}
                {[
                    { id: 1, name: 'Pizza', price: 10, imgSrc: 'path/to/image1.jpg' },
                    { id: 2, name: 'Burger', price: 8, imgSrc: 'path/to/image2.jpg' }
                ].map((product) => (
                    <Product key={product.id} product={product} onProductClick={handleProductClick} />
                ))}
            </div>

            {/* Modal produit */}
            {selectedProduct && (
                <Modal 
                    product={selectedProduct} 
                    onClose={handleCloseProductModal} 
                    onAddToCart={handleAddToCart} 
                />
            )}

            {/* Panier */}
            {isCartOpen && (
                <Cart 
                    cart={cart} 
                    onClose={handleCloseCart} 
                    onProceedToDelivery={handleOpenDeliveryModal} 
                />
            )}

            {/* Modale de livraison */}
            {isDeliveryModalOpen && (
                <DeliveryModal 
                    onClose={handleCloseDeliveryModal} 
                    onConfirm={() => alert("Commande confirmée !")} 
                />
            )}
        </div>
    );
}

export default App;
