// src/components/CartPage.js
import React from 'react';
import './cart.css';

function CartPage({ cart, onProceedToDelivery, onCancel }) {
    const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="cart-page">
            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img src={item.imgSrc} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <span className="cart-item-name">{item.name}</span>
                                <span className="cart-item-quantity">Quantité: {item.quantity}</span>
                                <span className="cart-item-price">{item.price}€</span>
                                <span className="cart-item-total">Total: {item.quantity * item.price}€</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>
            {cart.length > 0 && (
                <div className="cart-footer">
                    <h3>Total: {totalAmount}€</h3>
                    <button className="cart-button validate" onClick={onProceedToDelivery}>Valider commande</button>
                    <button className="cart-button cancel" onClick={onCancel}>Annuler</button>
                </div>
            )}
        </div>
    );
}

export default CartPage;
