import React, { useState } from 'react';
import './ProductList'
const ProductCard = ({ product, onAddToCart }) => {
  const [showModal, setShowModal] = useState(false);
  const [quantity, setQuantity] = useState(1);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showVideo, setShowVideo] = useState(false);

  const discount = Math.round(
    ((product.originalPrice - product.price) / product.originalPrice) * 100
  );

  const handleAddToCart = () => {
    onAddToCart(product, quantity);
    setShowModal(false);
  };

  const nextImage = () => {
    setCurrentImageIndex((currentImageIndex + 1) % product.images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((currentImageIndex - 1 + product.images.length) % product.images.length);
  };

  return (
    <>
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="relative">
          <img
            src={product.images[0]}
            alt={product.name}
            className="w-full h-48 object-cover"
          />
          {discount > 0 && (
            <div className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 rounded-md text-sm">
              -{discount}%
            </div>
          )}
        </div>
        <div className="p-4">
          <h3 className="text-lg font-semibold text-gray-900">{product.name}</h3>
          <p className="mt-1 text-gray-500 line-clamp-2">{product.description}</p>
          <div className="mt-4 flex items-center justify-between">
            <div className="flex flex-col">
              <span className="text-xl font-bold text-indigo-600">
                ${(product.price / 100).toFixed(2)}
              </span>
              {discount > 0 && (
                <span className="text-sm text-gray-500 line-through">
                  ${(product.originalPrice / 100).toFixed(2)}
                </span>
              )}
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => setShowModal(true)}
                className="p-2 text-gray-500 hover:text-indigo-600"
              >
                View
              </button>
              <button
                onClick={() => onAddToCart(product, 1)}
                className="flex items-center space-x-1 bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors"
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-500 bg-opacity-50">
          <div
            className="bg-white rounded-lg shadow-xl p-6 relative max-w-2xl w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={() => setShowModal(false)}
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-800"
            >
              &times;
            </button>
            <div>
              {showVideo ? (
                <video src={product.videoUrl} controls className="w-full h-64" />
              ) : (
                <div className="relative">
                  <button
                    className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full"
                    onClick={prevImage}
                  >
                    &#10094;
                  </button>
                  <img
                    src={product.images[currentImageIndex]}
                    alt={product.name}
                    className="w-full h-64 object-cover"
                  />
                  <button
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full"
                    onClick={nextImage}
                  >
                    &#10095;
                  </button>
                </div>
              )}
              <div className="mt-4">
                <h3 className="text-2xl font-bold text-gray-900">{product.name}</h3>
                <p className="mt-2 text-gray-600">{product.description}</p>
                <div className="mt-4">
                  <span className="text-2xl font-bold text-indigo-600">
                    ${(product.price / 100).toFixed(2)}
                  </span>
                  {discount > 0 && (
                    <span className="ml-2 text-lg text-gray-500 line-through">
                      ${(product.originalPrice / 100).toFixed(2)}
                    </span>
                  )}
                </div>
              </div>
              <div className="mt-4 flex space-x-2 items-center">
                <label htmlFor="quantity" className="font-medium">
                  Quantity:
                </label>
                <input
                  type="number"
                  id="quantity"
                  min="1"
                  value={quantity}
                  onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
                  className="w-16 px-2 py-1 border rounded"
                />
              </div>
              <div className="mt-4 flex justify-between">
                <button
                  onClick={handleAddToCart}
                  className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => setShowVideo(!showVideo)}
                  className="bg-gray-200 px-4 py-2 rounded-md hover:bg-gray-300"
                >
                  {showVideo ? 'View Image' : 'View Video'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ProductCard;
