// src/components/CartPage.js
import React from 'react';
import './cart.css';

function CartPage({ cart, onProceedToDelivery, onCancel }) {
    const calculateTotal = () => {
        return cart.reduce((acc, item) => acc + item.quantity * item.price, 0);
    };

    const calculateTotalItems = () => {
        return cart.reduce((acc, item) => acc + item.quantity, 0);
    };

    return (
        <div className="cart-page">
            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img src={item.imgSrc} alt={item.name} className="cart-item-image" />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantité: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price}€</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price}€</span>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>
            <div className="cart-summary">
                <h3 className="total-general">
                    Total ({calculateTotalItems()} articles) = {calculateTotal()}€
                </h3>
                <button onClick={onProceedToDelivery}>Valider commande</button>
                <button onClick={onCancel}>Annuler</button>
            </div>
        </div>
    );
}

export default CartPage;
