import React from 'react';
import './cart.css';

function Cart({ cart, onClose, onProceedToDelivery }) {
    return (
        <div className="cart-container">
            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <span>{item.name}</span>
                            <span>{item.quantity} x {item.price}€</span>
                            <span>Total: {item.quantity * item.price}€</span>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>
            <button onClick={onProceedToDelivery}>Valider commande</button>
            <button onClick={onClose}>Fermer</button>
        </div>
    );
}

export default Cart;
