// src/components/CartPage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './cart.css';

function CartPage({ cart }) {
    const navigate = useNavigate();

    const calculateTotal = () => {
        return cart.reduce((acc, item) => acc + item.quantity * item.price, 0);
    };

    return (
        <div className="cart-page">
            <button className="back-button" onClick={() => navigate(-1)}>Retour</button>

            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img src={item.imgSrc} alt={item.name} className="cart-item-image" />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantité: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price}€</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price}€</span>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>
            <div className="cart-summary">
                <span className="total-general">Total Général ({cart.length} articles) = {calculateTotal()}€</span>
                <button className="validate-button" onClick={() => navigate('/delivery')}>Valider commande</button>
            </div>
        </div>
    );
}

export default CartPage;
