import React, { useState } from 'react';

const ValidateOrder = ({ items, totalPrice }) => {
  const [deliveryInfo, setDeliveryInfo] = useState({ name: '', surname: '', city: '', neighborhood: '', phone: '' });
  const [message, setMessage] = useState(''); // État pour afficher les messages dans le composant

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDeliveryInfo({ ...deliveryInfo, [name]: value });
  };

  const handleOrderSubmit = async () => {
    console.log('Envoi des données de commande au backend depuis ValidateOrder.js...');
    console.log('Données de commande :', { items, deliveryInfo, totalPrice });
    setMessage('Envoi de la commande...');

    try {
      const response = await fetch('http://localhost:3000/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ items, deliveryInfo, totalPrice }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Réponse reçue du backend :', data);
        setMessage('Commande validée avec succès !');
      } else {
        console.log('Erreur de serveur lors de la création de la commande');
        setMessage('Erreur lors de la validation de la commande');
      }
    } catch (error) {
      console.error('Erreur lors de la communication avec le serveur:', error);
      setMessage('Impossible de se connecter au serveur');
    }
  };

  return (
    <div>
      <h2>Valider la commande</h2>
      <input name="name" placeholder="Nom" onChange={handleInputChange} />
      <input name="surname" placeholder="Prénom" onChange={handleInputChange} />
      <input name="city" placeholder="Ville" onChange={handleInputChange} />
      <input name="neighborhood" placeholder="Quartier" onChange={handleInputChange} />
      <input name="phone" placeholder="Téléphone" onChange={handleInputChange} />
      <button onClick={handleOrderSubmit}>Valider</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ValidateOrder;
