import React, { useState } from 'react';

const ValidateOrder = ({ items, totalPrice, onEmptyCart }) => {
  const [deliveryInfo, setDeliveryInfo] = useState({
    name: '',
    surname: '',
    city: '',
    neighborhood: '',
    phone: '',
  });
  const [message, setMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false); // État pour gérer l'état de soumission

  // Gérer les changements dans les champs de saisie
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDeliveryInfo({ ...deliveryInfo, [name]: value });
  };

  // Gérer la soumission de la commande
  const handleOrderSubmit = async () => {
    console.log('Envoi des données de commande au backend...');
    console.log('Données de commande :', { items, deliveryInfo, totalPrice });
    setMessage('Envoi de la commande...');
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5000/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ items, deliveryInfo, totalPrice }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Réponse reçue du backend :', data);
        setMessage('Commande validée avec succès !');
        onEmptyCart(); // Vider le panier après une commande réussie
      } else {
        console.log('Erreur de serveur lors de la création de la commande');
        setMessage('Erreur lors de la validation de la commande. Veuillez réessayer.');
      }
    } catch (error) {
      console.error('Erreur lors de la communication avec le serveur:', error);
      setMessage('Impossible de se connecter au serveur. Vérifiez votre connexion.');
    } finally {
      setIsSubmitting(false); // Fin de la soumission
    }
  };

  return (
    <div>
      <h2>Valider la commande</h2>
      <div>
        <input
          name="name"
          placeholder="Nom"
          onChange={handleInputChange}
          value={deliveryInfo.name}
        />
        <input
          name="surname"
          placeholder="Prénom"
          onChange={handleInputChange}
          value={deliveryInfo.surname}
        />
        <input
          name="city"
          placeholder="Ville"
          onChange={handleInputChange}
          value={deliveryInfo.city}
        />
        <input
          name="neighborhood"
          placeholder="Quartier"
          onChange={handleInputChange}
          value={deliveryInfo.neighborhood}
        />
        <input
          name="phone"
          placeholder="Téléphone"
          onChange={handleInputChange}
          value={deliveryInfo.phone}
        />
      </div>

      <button onClick={handleOrderSubmit} disabled={isSubmitting}>
        {isSubmitting ? 'Envoi en cours...' : 'Valider la commande'}
      </button>

      {message && <p>{message}</p>}
    </div>
  );
};

export default ValidateOrder;
