import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './cart.css';

function CartPage({ cart, onRemoveFromCart }) {
    const navigate = useNavigate();
    const [errorMessage, setErrorMessage] = useState('');
    const [promoCode, setPromoCode] = useState('');
    const [isPromoValid, setIsPromoValid] = useState(null);

    // Log des props re√ßues
    console.group('üîÑ Initialisation CartPage');
    console.log('üì• Props re√ßues:', { cart, onRemoveFromCart });
    console.groupEnd();

    if (!cart) {
        console.error("‚ùå Erreur: cart est undefined!");
        return <p>Erreur : Le panier ne peut pas √™tre charg√©.</p>;
    }

    console.log("üìã Contenu initial du panier:", cart);

    const validPromoCodes = {
        "ISIRA07": 0.9, // 10% de r√©duction
        "FREESHIP": 1   // Prix normal
    };

    const calculateTotal = () => {
        let total = cart.reduce((acc, item) => {
            console.log(`üí∞ Calcul pour ${item.name}: ${item.quantity} x ${item.price} FCFA`);
            return acc + item.quantity * item.price;
        }, 0);

        if (isPromoValid !== null && validPromoCodes[promoCode]) {
            const reduction = (1 - validPromoCodes[promoCode]) * 100;
            console.log(`üè∑Ô∏è Application du code promo: -${reduction}%`);
            total *= validPromoCodes[promoCode];
        }

        console.log(`üíµ Total calcul√©: ${total} FCFA`);
        return total;
    };

    const handlePromoChange = (e) => {
        const code = e.target.value.toUpperCase();
        console.group('üé´ V√©rification code promo');
        console.log('Code saisi:', code);

        setPromoCode(code);
        if (validPromoCodes[code]) {
            console.log('‚úÖ Code promo valide');
            setIsPromoValid(true);
            setErrorMessage('');
        } else {
            console.log('‚ùå Code promo invalide');
            setIsPromoValid(false);
            setErrorMessage('Code promo invalide.');
        }
        console.groupEnd();
    };

    const handleProceedToDelivery = () => {
        console.group('üöÄ Proc√©dure de livraison');

        if (cart.length > 0) {
            const orderData = {
                cart: cart,
                totalPrice: calculateTotal(),
                appliedPromo: promoCode || null
            };

            console.group('üì¶ D√©tails de la commande');
            console.log('Articles dans le panier:');
            cart.forEach((item, index) => {
                console.group(`üìù Article ${index + 1}: ${item.name}`);
                console.log('ID:', item.id);
                console.log('Quantit√©:', item.quantity);
                console.log('Prix unitaire:', item.price, 'FCFA');
                console.log('Sous-total:', item.quantity * item.price, 'FCFA');
                console.log('Image:', item.imgSrcs[0]);
                console.groupEnd();
            });

            console.log('üí∞ Montant total:', calculateTotal(), 'FCFA');
            console.log('üè∑Ô∏è Code promo:', promoCode || 'Aucun');

            if (promoCode && validPromoCodes[promoCode]) {
                console.log('üìä R√©duction appliqu√©e:', 
                    ((1 - validPromoCodes[promoCode]) * 100) + '%');
            }

            console.log('üì§ Donn√©es compl√®tes envoy√©es:', orderData);
            console.groupEnd();

            navigate('/delivery', { state: orderData });
            console.log('‚úÖ Navigation vers la page de livraison');
        } else {
            console.warn("‚ö†Ô∏è Tentative de validation d'un panier vide");
            setErrorMessage('Votre panier est vide. Ajoutez des articles avant de valider la commande.');
        }

        console.groupEnd();
    };

    console.log("üîÑ Rendu CartPage");

    return (
        <div className="cart-page">
            <button 
                className="back-button" 
                onClick={() => {
                    console.log('‚¨ÖÔ∏è Retour √† la page pr√©c√©dente');
                    navigate(-1);
                }}
            >
                Retour
            </button>

            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img 
                                src={`${process.env.PUBLIC_URL}${item.imgSrcs[0]}`} 
                                alt={item.name} 
                                className="cart-item-image" 
                            />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantit√©: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price} FCFA</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price} FCFA</span>
                            <button 
                                onClick={() => {
                                    console.log(`üóëÔ∏è Suppression de l'article: ${item.name}`);
                                    onRemoveFromCart(item.id);
                                }} 
                                className="remove-button"
                            >
                                Retirer
                            </button>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>

            <div className="promo-section">
                <input 
                    type="text" 
                    placeholder="Code promo" 
                    value={promoCode} 
                    onChange={handlePromoChange} 
                    className={`promo-input ${
                        isPromoValid === true ? "valid-input" : 
                        isPromoValid === false ? "invalid-input" : ""
                    }`}
                    style={{
                        borderRadius: '8px',
                        border: '2px solid orange',
                        padding: '8px',
                        width: '200px',
                        textAlign: 'center',
                        color: isPromoValid === true ? 'green' : 
                               isPromoValid === false ? 'red' : 'black'
                    }}
                />
                {errorMessage && (
                    <p className="error-message">{errorMessage}</p>
                )}
            </div>

            <div className="cart-summary">
                <span className="total-general">
                    Total G√©n√©ral ({cart.length} articles) = 
                    <span className={
                        isPromoValid === true ? "valid-total" : 
                        isPromoValid === false ? "invalid-total" : ""
                    }>
                        {calculateTotal()} FCFA
                    </span>
                </span>
                <button 
                    className="validate-button" 
                    onClick={handleProceedToDelivery}
                >
                    Proc√©der √† la livraison
                </button>
            </div>
        </div>
    );
}

export default CartPage;
