import React, { useState } from 'react';
import axios from 'axios';

const ValidateOrder = ({ cartItems, totalPrice }) => {
  const [deliveryInfo, setDeliveryInfo] = useState({
    name: "",
    surname: "",
    city: "",
    neighborhood: "",
    phone: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDeliveryInfo({ ...deliveryInfo, [name]: value });
  };

  const envoyerCommande = async () => {
    const orderData = {
      items: cartItems,
      deliveryInfo: deliveryInfo,
      totalPrice: totalPrice,
    };

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/orders`, orderData);
      console.log("Commande envoyée avec succès :", response.data);
      alert("Commande validée !");
    } catch (error) {
      console.error("Erreur lors de l'envoi de la commande :", error);
      alert("Erreur lors de la validation de la commande.");
    }
  };

  return (
    <div>
      <h2>Informations de livraison</h2>
      <input name="name" placeholder="Nom" onChange={handleChange} />
      <input name="surname" placeholder="Prénom" onChange={handleChange} />
      <input name="city" placeholder="Ville" onChange={handleChange} />
      <input name="neighborhood" placeholder="Quartier" onChange={handleChange} />
      <input name="phone" placeholder="Téléphone" onChange={handleChange} />
      <button onClick={envoyerCommande}>Valider la commande</button>
    </div>
  );
};

export default ValidateOrder;
