import React, { useState } from 'react';
import { ShoppingCart } from 'lucide-react';
import '../menu.css';

function Modal({ product, onClose, onAddToCart }) {
  const [quantity, setQuantity] = useState(1);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  if (!product) return null;

  const handleAddToCart = () => {
    onAddToCart(product, quantity);
    onClose();
  };

  const nextImage = () => {
    setCurrentImageIndex((currentImageIndex + 1) % product.imgSrcs.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((currentImageIndex - 1 + product.imgSrcs.length) % product.imgSrcs.length);
  };

  const discount = Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100);

  return (
    <>
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <div className="relative image-container">
            <button className="nav-arrow left-arrow" onClick={prevImage}>&#10094;</button>
            <img src={product.imgSrcs[currentImageIndex]} alt={product.name} className="modal-product-image" />
            <button className="nav-arrow right-arrow" onClick={nextImage}>&#10095;</button>
            {discount > 0 && (
              <div className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 rounded-md text-sm">
                -{discount}%
              </div>
            )}
          </div>
          <div className="p-4">
            <h2 className="text-2xl font-bold text-blue-900">{product.name}</h2>
            <p className="mt-2 text-gray-600">{product.modalDescription || product.description}</p>
            <div className="mt-4 flex items-center justify-between">
              <div className="flex flex-col">
                <span className="text-xl font-bold text-blue-600">
                  {product.price.toLocaleString()} FCFA
                </span>
                {discount > 0 && (
                  <span className="text-sm text-gray-500 line-through">
                    {product.originalPrice.toLocaleString()} FCFA
                  </span>
                )}
              </div>
              <button
                onClick={handleAddToCart}
                className="flex items-center space-x-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
              >
                <ShoppingCart className="h-5 w-5" />
                <span>Ajouter au panier</span>
              </button>
            </div>
            <div className="quantity-container mt-4">
              <label>Quantité:</label>
              <input
                type="number"
                value={quantity}
                min="1"
                onChange={(e) => setQuantity(parseInt(e.target.value))}
                className="border-gray-300 focus:border-blue-500 focus:ring-blue-500 rounded-md"
              />
            </div>
            <div className="view-buttons mt-4">
              <button onClick={() => setCurrentImageIndex(0)} className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2">
                Image
              </button>
              <button onClick={() => setCurrentImageIndex(-1)} className="bg-blue-500 text-white px-4 py-2 rounded-md">
                Vidéo
              </button>
            </div>
            <div className="images-container mt-4 flex overflow-x-auto">
              {product.imgSrcs.map((src, index) => (
                <img
                  key={index}
                  src={src}
                  alt={product.name}
                  className={`thumbnail-image ${index === currentImageIndex ? 'border-blue-500' : ''}`}
                  onClick={() => setCurrentImageIndex(index)}
                />
              ))}
            </div>
            {currentImageIndex === -1 && (
              <video src={product.videoSrc} controls className="modal-product-video"></video>
            )}
            <div className="modal-buttons mt-4">
              <button onClick={handleAddToCart} className="bg-blue-600 text-white px-4 py-2 rounded-md mr-2">
                Ajouter au panier
              </button>
              <button onClick={onClose} className="bg-gray-500 text-white px-4 py-2 rounded-md">
                Annuler
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Modal;
