            <div id="cart-items">
                {cart.map((item, index) => (
                    <div key={index}>{item.name} - {item.quantity} x {item.price}â‚¬</div>
                ))}
            </div>
            <button onClick={onProceedToDelivery}>Valider commande</button>
            <button onClick={onClose}>Fermer</button>
        </div>
    );
}

export default Cart;
