import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './cart.css';

function CartPage({ cart, onRemoveFromCart }) {
    const navigate = useNavigate();
    const [promoCode, setPromoCode] = useState('');
    const [isPromoValid, setIsPromoValid] = useState(null);
    const [appliedPromo, setAppliedPromo] = useState('');
    const [errorMessage, setErrorMessage] = useState(''); // Ajout du message d'erreur

    const validPromoCodes = {
        "PROMO10": 0.9,
        "FREESHIP": 1
    };

    const calculateTotal = () => {
        let total = cart.reduce((acc, item) => acc + item.quantity * item.price, 0);
        if (isPromoValid) {
            total *= validPromoCodes[promoCode];
        }
        return total;
    };

    const handlePromoChange = (e) => {
        const code = e.target.value.toUpperCase();
        setPromoCode(code);
        if (validPromoCodes[code]) {
            setIsPromoValid(true);
            setAppliedPromo(code);
        } else {
            setIsPromoValid(false);
            setAppliedPromo('');
        }
    };

    const handleProceedToDelivery = () => {
        if (cart.length === 0) {
            setErrorMessage("Votre panier est vide. Veuillez ajouter des articles avant de continuer.");
            return;
        }
        setErrorMessage('');

        // Envoi des données au backend, y compris le code promo
        const orderData = {
            items: cart.map(item => ({
                name: item.name,
                price: item.price,
                quantity: item.quantity
            })),
            deliveryInfo: { 
                name: "John", // Remplace par des valeurs dynamiques
                surname: "Doe", // Remplace par des valeurs dynamiques
                city: "Paris", // Remplace par des valeurs dynamiques
                neighborhood: "Example", // Remplace par des valeurs dynamiques
                phone: "123456789" // Remplace par des valeurs dynamiques
            },
            totalPrice: calculateTotal(),
            promoCode: appliedPromo // Envoi du code promo ici
        };

        fetch('/api/orders', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(orderData)
        })
        .then(response => response.json())
        .then(data => {
            if (data.message === 'Commande validée avec succès') {
                navigate('/confirmation', { state: { orderId: data.orderId } });
            } else {
                setErrorMessage(data.message || 'Erreur lors de la soumission de la commande.');
            }
        })
        .catch(error => {
            console.error('Erreur:', error);
            setErrorMessage('Une erreur s\'est produite lors de la soumission de la commande.');
        });
    };

    return (
        <div className="cart-page">
            <button className="back-button" onClick={() => navigate(-1)}>Retour</button>

            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img src={`${process.env.PUBLIC_URL}${item.imgSrcs[0]}`} alt={item.name} className="cart-item-image" />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantité: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price} FCFA</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price} FCFA</span>
                            <button onClick={() => onRemoveFromCart(item.id)} className="remove-button">
                                Supprimer
                            </button>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>

            <div className="promo-section">
                <input 
                    type="text" 
                    placeholder="Code promo" 
                    value={promoCode} 
                    onChange={handlePromoChange} 
                    className={`promo-input ${isPromoValid === true ? "valid-input" : isPromoValid === false ? "invalid-input" : ""}`}
                    style={{
                        borderRadius: '8px',
                        border: '2px solid orange',
                        padding: '8px',
                        width: '200px',
                        textAlign: 'center',
                        color: isPromoValid === true ? 'green' : isPromoValid === false ? 'red' : 'black'
                    }}
                />
            </div>

            <div className="cart-summary">
                <span className="total-general">Total Général ({cart.length} articles) = <span className={isPromoValid === true ? "valid-total" : isPromoValid === false ? "invalid-total" : ""}>{calculateTotal()} FCFA</span></span>

                {errorMessage && <p className="error-message">{errorMessage}</p>}

                <button className="validate-button" onClick={handleProceedToDelivery}>
                    Procéder à la livraison
                </button>
            </div>
        </div>

    );
}

export default CartPage;
