// src/components/DeliveryPage.js
import React, { useState } from 'react';
import './delivery.css';

function DeliveryPage({ onConfirmDelivery }) {
    const [deliveryInfo, setDeliveryInfo] = useState({
        firstName: '',
        lastName: '',
        city: '',
        neighborhood: '',
        phoneNumber: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setDeliveryInfo((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (Object.values(deliveryInfo).every((value) => value)) {
            onConfirmDelivery(deliveryInfo);
        } else {
            alert('Please fill in all fields.');
        }
    };

    return (
        <div className="delivery-page">
            <h2>Enter Delivery Information</h2>
            <form onSubmit={handleSubmit}>
                <label>First Name</label>
                <input name="firstName" value={deliveryInfo.firstName} onChange={handleChange} required />

                <label>Last Name</label>
                <input name="lastName" value={deliveryInfo.lastName} onChange={handleChange} required />

                <label>City</label>
                <input name="city" value={deliveryInfo.city} onChange={handleChange} required />

                <label>Neighborhood</label>
                <input name="neighborhood" value={deliveryInfo.neighborhood} onChange={handleChange} required />

                <label>Phone Number</label>
                <input name="phoneNumber" value={deliveryInfo.phoneNumber} onChange={handleChange} required />

                <button type="submit">Confirm Delivery</button>
            </form>
        </div>
    );
}

export default DeliveryPage;
