// src/components/DeliveryPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './delivery.css';

function DeliveryPage() {
    const [deliveryInfo, setDeliveryInfo] = useState({
        firstName: '',
        lastName: '',
        city: '',
        neighborhood: '',
        phone: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setDeliveryInfo({ ...deliveryInfo, [e.target.name]: e.target.value });
    };

    const handleConfirm = () => {
        if (Object.values(deliveryInfo).every(value => value)) {
            alert('Commande confirmée !');
            navigate('/'); // Redirige vers la page d'accueil après la confirmation
        } else {
            alert('Veuillez remplir tous les champs.');
        }
    };

    return (
        <div className="delivery-page">
            <h2>Informations de Livraison</h2>
            <form>
                <label>Nom</label>
                <input name="lastName" value={deliveryInfo.lastName} onChange={handleChange} required />

                <label>Prénom</label>
                <input name="firstName" value={deliveryInfo.firstName} onChange={handleChange} required />

                <label>Ville</label>
                <input name="city" value={deliveryInfo.city} onChange={handleChange} required />

                <label>Quartier</label>
                <input name="neighborhood" value={deliveryInfo.neighborhood} onChange={handleChange} required />

                <label>Numéro de téléphone</label>
                <input name="phone" value={deliveryInfo.phone} onChange={handleChange} required />
            </form>
            <button onClick={handleConfirm}>Confirmer la commande</button>
            <button onClick={() => navigate(-1)}>Annuler</button>
        </div>
    );
}

export default DeliveryPage;
