import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, Link } from 'react-router-dom'; // Importation de la bibliothèque react-router-dom
import './menu.css';
import CartPage from './components/CartPage';
import SignUpPage from './components/formulaire/SignUpPage'; // Importation du formulaire d'inscription
import Modal from './components/Modal';
import DeliveryPage from './components/DeliveryPage';
import ValidateOrder from './components/ValidateOrder';
import products from './productsData';

function App() {
    const [cart, setCart] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('tous');
    const [shuffledProducts, setShuffledProducts] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false); // L'état pour vérifier si l'utilisateur est connecté

    // Mélanger les produits une seule fois au premier rendu
    useEffect(() => {
        const shuffleProducts = (products) => {
            return [...products].sort(() => Math.random() - 0.5);
        };
        setShuffledProducts(shuffleProducts(products));
    }, []);

    // Fonction pour ajouter un produit au panier
    const addToCart = (product, quantity) => {
        setCart(prevCart => {
            const existingProductIndex = prevCart.findIndex(item => item.id === product.id);
            if (existingProductIndex >= 0) {
                const updatedCart = [...prevCart];
                updatedCart[existingProductIndex].quantity += quantity;
                return updatedCart;
            } else {
                return [...prevCart, { ...product, quantity }];
            }
        });
    };

    // Fonction pour supprimer un produit du panier
    const removeFromCart = (productId) => {
        setCart(prevCart => prevCart.filter(item => item.id !== productId));
    };

    // Fonction pour vider le panier
    const emptyCart = () => {
        setCart([]);
    };

    // Calculer le nombre total d'articles dans le panier
    const totalItemsInCart = cart.reduce((total, item) => total + item.quantity, 0);

    // Calculer le prix total de la commande
    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    // Gérer les redirections selon l'état de connexion
    const handleValidationRedirect = () => {
        if (!isAuthenticated) {
            return <Navigate to="/login" />;
        } else {
            return <Navigate to="/delivery" />;
        }
    };

    return (
        <div className="App">
            <div className="top-bar-first-line">
                <div className="logo-line">
                    <img src="link_logo.png" alt="Logo Whilf Rede" />
                </div>
                <input
                    type="text"
                    className="search-bar"
                    placeholder="Rechercher un produit ...."
                    onChange={e => setSearchQuery(e.target.value)}
                />
                <Link to="/cart" className="cart-icon">
                    <i className="fas fa-shopping-cart"></i>
                    <span className="cart-count">{totalItemsInCart}</span>
                </Link>
                {/* Afficher le bouton se connecter uniquement si l'utilisateur n'est pas connecté */}
                {!isAuthenticated && (
                    <Link to="/login" className="login-button">Se connecter</Link>
                )}
            </div>

            <div className="separator"></div>

            {/* Affichage des filtres de produits */}
            <div className="top-bar-second-line">
                <div className="filter-container">
                    {['tous', 'vetement', 'cuisine', 'electronic', 'decoration', 'mecanique'].map(category => (
                        <button
                            key={category}
                            className={`filter-button ${filter === category ? 'active' : ''}`}
                            onClick={() => setFilter(category)}
                        >
                            {category.charAt(0).toUpperCase() + category.slice(1)}
                        </button>
                    ))}
                </div>
            </div>

            <Routes>
                {/* Page d'affichage des produits */}
                <Route path="/menu" element={
                    <div className="content">
                        {/* Afficher les produits filtrés */}
                        {shuffledProducts.map(product => (
                            <Product key={product.id} product={product} />
                        ))}
                    </div>
                } />

                {/* Page de panier */}
                <Route path="/cart" element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />} />

                {/* Page de validation de commande */}
                <Route path="/validate-order" element={handleValidationRedirect()} />

                {/* Page de livraison */}
                <Route path="/delivery" element={<DeliveryPage />} />

                {/* Formulaire de connexion (si non authentifié) */}
                <Route path="/login" element={<SignUpPage setIsAuthenticated={setIsAuthenticated} />} />
            </Routes>

            {/* Modal pour afficher les détails d'un produit sélectionné */}
            {selectedProduct && (
                <Modal product={selectedProduct} onClose={() => setSelectedProduct(null)} onAddToCart={addToCart} />
            )}
        </div>
    );
}

export default App;
