import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './cart.css';

function CartPage({ cart, onRemoveFromCart }) {
    const navigate = useNavigate();
    const [errorMessage, setErrorMessage] = useState('');
    const [promoCode, setPromoCode] = useState('');
    const [isPromoValid, setIsPromoValid] = useState(null);

    console.log("Props re√ßues dans CartPage :", { cart, onRemoveFromCart });

    if (!cart) {
        console.log("Erreur : cart est undefined !");
        return <p>Erreur : Le panier ne peut pas √™tre charg√©.</p>;
    }

    console.log("Contenu du panier :", cart);

    const validPromoCodes = {
        "ISIRA07": 0.9,
        "FREESHIP": 1
    };

    const calculateTotal = () => {
        let total = cart.reduce((acc, item) => acc + item.quantity * item.price, 0);
        if (isPromoValid !== null && validPromoCodes[promoCode]) {
            total *= validPromoCodes[promoCode];
        }
        return total;
    };

    const handlePromoChange = (e) => {
        const code = e.target.value.toUpperCase();
        setPromoCode(code);
        if (validPromoCodes[code]) {
            setIsPromoValid(true);
            setErrorMessage('');
        } else {
            setIsPromoValid(false);
            setErrorMessage('Code promo invalide.');
        }
    };

    const handleProceedToDelivery = () => {
        if (cart.length > 0) {
          const orderData = { 
            cart: cart, 
            totalPrice: calculateTotal(), 
            appliedPromo: promoCode || null 
          };

          console.log("üõí Donn√©es envoy√©es vers le backend :");
          console.log("üõçÔ∏è Contenu du panier :", JSON.stringify(cart, null, 2));
          console.log("üí∞ Prix total apr√®s r√©duction :", calculateTotal(), "FCFA");
          console.log("üéüÔ∏è Code promo appliqu√© :", promoCode ? promoCode : "Aucun");

          navigate('/delivery', { state: orderData });
        } else {
          setErrorMessage('Votre panier est vide. Ajoutez des articles avant de valider la commande.');
          console.log("‚ö†Ô∏è Panier vide, commande non valid√©e.");
        }
      };


    console.log("CartPage rendu !");

    return (
        <div className="cart-page">
            <button className="back-button" onClick={() => navigate(-1)}>Retour</button>

            <h2>Votre Panier</h2>
            <div id="cart-items">
                {cart.length > 0 ? (
                    cart.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img src={`${process.env.PUBLIC_URL}${item.imgSrcs[0]}`} alt={item.name} className="cart-item-image" />
                            <span className="cart-item-name">{item.name}</span>
                            <span className="cart-item-quantity">Quantit√©: {item.quantity}</span>
                            <span className="cart-item-price">Prix unitaire: {item.price} FCFA</span>
                            <span className="cart-item-total">Total: {item.quantity * item.price} FCFA</span>
                            <button onClick={() => onRemoveFromCart(item.id)} className="remove-button">
                                Retirer
                            </button>
                        </div>
                    ))
                ) : (
                    <p>Votre panier est vide</p>
                )}
            </div>

            <div className="promo-section">
                <input 
                    type="text" 
                    placeholder="Code promo" 
                    value={promoCode} 
                    onChange={handlePromoChange} 
                    className={`promo-input ${isPromoValid === true ? "valid-input" : isPromoValid === false ? "invalid-input" : ""}`}
                    style={{
                        borderRadius: '8px',
                        border: '2px solid orange',
                        padding: '8px',
                        width: '200px',
                        textAlign: 'center',
                        color: isPromoValid === true ? 'green' : isPromoValid === false ? 'red' : 'black'
                    }}
                />
                {errorMessage && <p className="error-message">{errorMessage}</p>}
            </div>

            <div className="cart-summary">
                <span className="total-general">
                    Total G√©n√©ral ({cart.length} articles) = <span className={isPromoValid === true ? "valid-total" : isPromoValid === false ? "invalid-total" : ""}>
                        {calculateTotal()} FCFA
                    </span>
                </span>
                <button className="validate-button" onClick={handleProceedToDelivery}>
                    Proc√©der √† la livraison
                </button>
            </div>
        </div>
    );
}

export default CartPage;
