import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from 'moment';
import 'moment/locale/fr'; // Importer la locale française
import './admin.css';

moment.locale('fr'); // Configurer la locale à 'fr'

function AdminPage() {
    const [orders, setOrders] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get('https://link-repository.onrender.com/api/orders');
                if (response.status !== 200) {
                    throw new Error(`Erreur : ${response.status}`);
                }
                setOrders(response.data);
            } catch (error) {
                setErrorMessage(`Erreur lors du chargement des commandes. ${error.message}`);
            }
        };

        fetchOrders();
    }, []);

    const handleStatusChange = async (orderId, newStatus) => {
        try {
            await axios.put(`https://link-repository.onrender.com/api/orders/${orderId}/status`, { status: newStatus });
            setOrders(prevOrders =>
                prevOrders.map(order =>
                    order.id === orderId ? { ...order, status: newStatus } : order
                )
            );
        } catch (error) {
            setErrorMessage(`Erreur lors de la mise à jour du statut. ${error.message}`);
        }
    };

    const getStatusClassName = (status) => {
        switch (status) {
            case 'pending':
                return 'status-pending';
            case 'processed':
                return 'status-processed';
            case 'shipped':
                return 'status-shipped';
            case 'delivered':
                return 'status-delivered';
            case 'cancelled':
                return 'status-cancelled';
            case 'paid':
                return 'status-paid';
            default:
                return '';
        }
    };

    // Grouper les commandes par date
    const ordersByDate = orders.reduce((acc, order) => {
        const orderDate = moment(order.created_at).startOf('day').format('YYYY-MM-DD'); // Utiliser la date des commandes
        if (!acc[orderDate]) acc[orderDate] = [];
        acc[orderDate].push(order);
        return acc;
    }, {});

    return (
        <div className="admin-container">
            <h1>Gestion des Commandes</h1>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {Object.keys(ordersByDate).map(date => {
                const dailyOrders = ordersByDate[date];
                const dailyRevenue = dailyOrders.reduce((total, order) => total + parseFloat(order.total_price), 0);

                // Formatage de la date des commandes
                const formattedDate = moment(date, 'YYYY-MM-DD').format('dddd D MMMM YYYY'); // Format en français

                return (
                    <div key={date} className="daily-section">
                        <h2 className="order-date">{formattedDate}</h2>
                        <h3 className="daily-revenue">Chiffre d'affaires du {formattedDate} : {dailyRevenue.toFixed(2)} FCFA</h3>

                        <table className="orders-table">
                            <thead>
                                <tr>
                                    <th>Heure</th>
                                    <th>ID</th>
                                    <th>Client</th>
                                    <th>Ville</th>
                                    <th>Quartier</th>
                                    <th>Téléphone</th>
                                    <th>Total</th>
                                    <th>Statut</th>
                                    <th>Actions</th>
                                    <th>Produits</th>
                                </tr>
                            </thead>
                            <tbody>
                                {dailyOrders.map(order => {
                                    const className = getStatusClassName(order.status);
                                    const orderTimeString = moment(order.created_at).format('LT'); // Heure formatée

                                    return (
                                        <tr key={order.id} className={className}>
                                            <td>{orderTimeString}</td>
                                            <td>{order.id}</td>
                                            <td>{`${order.name} ${order.surname}`}</td>
                                            <td>{order.city}</td>
                                            <td>{order.neighborhood}</td>
                                            <td>{order.phone}</td>
                                            <td>{parseFloat(order.total_price).toFixed(2)} FCFA</td>
                                            <td>{order.status}</td>
                                            <td>
                                                <select
                                                    value={order.status}
                                                    onChange={e => handleStatusChange(order.id, e.target.value)}
                                                >
                                                    <option value="pending">En attente</option>
                                                    <option value="processed">Traitée</option>
                                                    <option value="shipped">Expédiée</option>
                                                    <option value="delivered">Livrée</option>
                                                    <option value="cancelled">Annulée</option>
                                                    <option value="paid">Payée</option>
                                                </select>
                                            </td>
                                            <td>
                                                <ul>
                                                    {order.products.map(product => (
                                                        <li key={product.product_name}>
                                                            {product.product_name} - {parseFloat(product.product_price).toFixed(2)} FCFA (Quantité : {product.quantity})
                                                        </li>
                                                    ))}
                                                </ul>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>
                );
            })}
        </div>
    );
}

export default AdminPage;
