import React, { useState } from 'react';

function Modal({ product, onClose, onAddToCart }) {
    const [quantity, setQuantity] = useState(1);

    return (
        <div className="modal">
            <img src={product.imgSrc} alt="Produit" className="modal-img" />
            <h2>{product.name}</h2>
            <p>{product.price}â‚¬</p>
            <input
                type="number"
                value={quantity}
                min="1"
                onChange={e => setQuantity(e.target.value)}
            />
            <button onClick={() => { onAddToCart(product, quantity); onClose(); }}>Ajouter au Panier</button>
            <button onClick={onClose}>Fermer</button>
        </div>
    );
}

export default Modal;
