import React, { useState, useMemo, useEffect } from 'react';
import { Routes, Route, Navigate, Link } from 'react-router-dom'; // Ne pas importer BrowserRouter ici
import './menu.css';
import ValidateOrder from './components/ValidateOrder';
import Product from './components/Product';
import CartPage from './components/CartPage';
import Modal from './components/Modal';
import DeliveryPage from './components/DeliveryPage';
import products from './productsData';

function App() {
    const [cart, setCart] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('tous');
    const [shuffledProducts, setShuffledProducts] = useState([]);

    // Mélanger les produits une seule fois au premier rendu
    useEffect(() => {
        const shuffleProducts = (products) => [...products].sort(() => Math.random() - 0.5);
        setShuffledProducts(shuffleProducts(products));
    }, []);

    // Nouveautés : récupérer les 5 premiers produits
    const nouveautes = useMemo(() => shuffledProducts.slice(0, 5), [shuffledProducts]);

    // Produits restants : exclure les nouveautés
    const restOfProducts = useMemo(() => shuffledProducts.slice(5), [shuffledProducts]);

    // Filtrer les produits restants par recherche et catégorie
    const filteredProducts = useMemo(() => {
        return restOfProducts.filter(
            product =>
                (filter === 'tous' || product.category === filter) &&
                (
                    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    product.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()))
                )
        );
    }, [restOfProducts, searchQuery, filter]);

    // Ajouter un produit au panier
    const addToCart = (product, quantity) => {
        setCart(prevCart => {
            const existingProductIndex = prevCart.findIndex(item => item.id === product.id);
            if (existingProductIndex >= 0) {
                const updatedCart = [...prevCart];
                updatedCart[existingProductIndex].quantity += quantity;
                return updatedCart;
            } else {
                return [...prevCart, { ...product, quantity }];
            }
        });
    };

    // Supprimer un produit du panier
    const removeFromCart = (productId) => {
        setCart(prevCart => prevCart.filter(item => item.id !== productId));
    };

    // Vider le panier
    const emptyCart = () => {
        setCart([]);
    };

    // Afficher les détails d'un produit dans le Modal
    const handleProductClick = product => {
        setSelectedProduct(product);
    };

    // Calculer le nombre total d'articles dans le panier
    const totalItemsInCart = cart.reduce((total, item) => total + item.quantity, 0);

    // Calculer le prix total de la commande
    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="App">
            {/* Barre de recherche et icône du panier */}
            <div className="top-bar-first-line">
                <div className="logo-line">
                    <img src="link_logo.png" alt="Logo Whilf Rede" />
                </div>
                <input
                    type="text"
                    className="search-bar"
                    placeholder="Rechercher un produit ..."
                    onChange={e => setSearchQuery(e.target.value)}
                />
                <Link to="/cart" className="cart-icon">
                    <i className="fas fa-shopping-cart"></i>
                    <span className="cart-count">{totalItemsInCart}</span>
                </Link>
            </div>

            <div className="separator"></div>

            {/* Boutons de filtrage */}
            <div className="top-bar-second-line">
                <div className="filter-container">
                    {['tous', 'vetement', 'cuisine', 'electronic', 'decoration', 'mecanique'].map(category => (
                        <button
                            key={category}
                            className={`filter-button ${filter === category ? 'active' : ''}`}
                            onClick={() => setFilter(category)}
                        >
                            {category.charAt(0).toUpperCase() + category.slice(1)}
                        </button>
                    ))}
                </div>
            </div>

            {/* Routes */}
            <Routes>
                <Route path="/" element={<Navigate to="/menu" />} />
                <Route
                    path="/menu"
                    element={
                        <div>
                            {/* Produits classiques */}
                            <div className="content">
                                <h2>Produits Classiques</h2>
                                {restOfProducts.slice(0, 5).map(product => (
                                    <Product key={product.id} product={product} onProductClick={handleProductClick} />
                                ))}
                            </div>

                            {/* Nouveautés */}
                            <div className="nouveautes-section">
                                <h2>Nouveautés</h2>
                                <div className="nouveautes-container">
                                    {nouveautes.map(product => (
                                        <Product key={product.id} product={product} onProductClick={handleProductClick} />
                                    ))}
                                </div>
                            </div>

                            {/* Produits filtrés restants */}
                            <div className="content">
                                <h2>Autres Produits</h2>
                                {filteredProducts.map(product => (
                                    <Product key={product.id} product={product} onProductClick={handleProductClick} />
                                ))}
                            </div>
                        </div>
                    }
                />
                <Route path="/cart" element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />} />
                <Route path="/delivery" element={<DeliveryPage />} />
                <Route
                    path="/validate-order"
                    element={<ValidateOrder cartItems={cart} totalPrice={totalPrice} onEmptyCart={emptyCart} />}
                />
            </Routes>

            {/* Modal */}
            {selectedProduct && (
                <Modal
                    product={selectedProduct}
                    onClose={() => setSelectedProduct(null)}
                    onAddToCart={addToCart}
                />
            )}
        </div>
    );
}

export default App;
