import React, { useState, useEffect, useMemo } from 'react'; // Gardez useMemo ici
import { Routes, Route, Navigate, Link } from 'react-router-dom';
import './menu.css';
import ValidateOrder from './components/ValidateOrder';
import Product from './components/Product';
import CartPage from './components/CartPage';
import DeliveryPage from './components/DeliveryPage';
import products from './productsData';
import LoginPage from './components/formulaire/login_register'; // Connexion
import SignUpPage from './components/formulaire/SignUpPage'; // Inscription

function App() {
    const [cart, setCart] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('tous');
    const [shuffledProducts, setShuffledProducts] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false); // État de connexion

    // Mélanger les produits une seule fois au premier rendu
    useEffect(() => {
        const shuffleProducts = (products) => {
            return [...products].sort(() => Math.random() - 0.5);
        };
        setShuffledProducts(shuffleProducts(products));
    }, []);

    // Filtrer les produits par catégorie, nom, et description
    const filteredProducts = useMemo(() => {
        const calculateRelevance = (product) => {
            let relevanceScore = 0;
            const searchLower = searchQuery.toLowerCase();

            if (product.name.toLowerCase().includes(searchLower)) relevanceScore += 3;
            if (product.category.toLowerCase().includes(searchLower)) relevanceScore += 2;
            if (product.description && product.description.toLowerCase().includes(searchLower)) relevanceScore += 1;

            return relevanceScore;
        };

        const filtered = shuffledProducts.filter(
            product =>
                (filter === 'tous' || product.category === filter) &&
                (
                    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    product.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()))
                )
        );

        const lessRelevantProducts = shuffledProducts.filter(
            product =>
                !(
                    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    product.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()))
                )
        );

        const sortedFilteredProducts = filtered.sort((a, b) => calculateRelevance(b) - calculateRelevance(a));
        return [...sortedFilteredProducts, ...lessRelevantProducts];
    }, [shuffledProducts, searchQuery, filter]);

    const removeFromCart = (productId) => {
        setCart(prevCart => prevCart.filter(item => item.id !== productId));
    };

    const emptyCart = () => {
        setCart([]);
    };

    const totalItemsInCart = cart.reduce((total, item) => total + item.quantity, 0);
    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="App">
            {/* Afficher la barre de recherche et les catégories uniquement si l'utilisateur est connecté */}
            {isLoggedIn && (
                <>
                    <div className="top-bar-first-line">
                        <div className="logo-line">
                            <img src="link_logo.png" alt="Logo Whilf Rede" />
                        </div>
                        <input
                            type="text"
                            className="search-bar"
                            placeholder="Rechercher un produit ...."
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                        <Link to="/cart" className="cart-icon">
                            <i className="fas fa-shopping-cart"></i>
                            <span className="cart-count">{totalItemsInCart}</span>
                        </Link>
                    </div>

                    <div className="separator"></div>

                    <div className="top-bar-second-line">
                        <div className="filter-container">
                            {['tous', 'vetement', 'cuisine', 'electronic', 'decoration', 'mecanique'].map(category => (
                                <button
                                    key={category}
                                    className={`filter-button ${filter === category ? 'active' : ''}`}
                                    onClick={() => setFilter(category)}
                                >
                                    {category.charAt(0).toUpperCase() + category.slice(1)}
                                </button>
                            ))}
                        </div>
                    </div>
                </>
            )}

            {/* Routes */}
            <Routes>
                {/* Rediriger directement vers le menu */}
                <Route
                    path="/"
                    element={<Navigate to="/menu" />}
                />

                {/* Page Menu */}
                <Route
                    path="/menu"
                    element={
                        <div className="content">
                            {filteredProducts.map(product => (
                                <Product key={product.id} product={product} />
                            ))}
                        </div>
                    }
                />
                {/* Page du panier */}
                <Route
                    path="/cart"
                    element={
                        isLoggedIn ? (
                            <CartPage
                                cart={cart}
                                onRemoveFromCart={removeFromCart}
                                totalItemsInCart={totalItemsInCart}
                                totalPrice={totalPrice}
                                emptyCart={emptyCart}
                            />
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                />
                {/* Page de livraison */}
                <Route path="/delivery" element={<DeliveryPage />} />
                {/* Page de validation de commande */}
                <Route
                    path="/validate-order"
                    element={
                        isLoggedIn ? (
                            <ValidateOrder
                                cartItems={cart}
                                totalPrice={totalPrice}
                                onEmptyCart={emptyCart}
                            />
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                />
                {/* Page de connexion */}
                <Route
                    path="/login"
                    element={<LoginPage onLogin={() => setIsLoggedIn(true)} />}
                />
                {/* Page d'inscription */}
                <Route
                    path="/signup"
                    element={<SignUpPage />}
                />
            </Routes>
        </div>
    );
}

export default App;
