// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './menu.css';
import Product from './components/Product';
import CartPage from './components/CartPage';
import Modal from './components/Modal';
import DeliveryPage from './components/DeliveryPage';

function App() {
    const [cart, setCart] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [filter, setFilter] = useState('tous');

    const products = [
        { id: 1, name: 'Spaghetti', price: 15, originalPrice: 20, category: 'traditionnel', imgSrc: './image/menu-1.png', description: 'Un plat de spaghettis savoureux avec sauce maison', modalDescription: 'Our special spaghetti with fresh ingredients and homemade sauce' },
        { id: 2, name: 'Viande', price: 15, category: 'traditionnel', imgSrc: './image/menu-3.png' },
        { id: 3, name: 'Sauce verte', price: 15, category: 'traditionnel', imgSrc: './image/menu-4.png' },
        { id: 4, name: 'Riz blanc', price: 15, category: 'traditionnel', imgSrc: './image/menu-5.png' },
        { id: 5, name: 'Plat Vegan', price: 10, category: 'vegan', imgSrc: './image/vegan-dish.jpg' },
        { id: 6, name: 'Dessert', price: 5, category: 'dessert', imgSrc: './image/dessert.jpg' }
    ];

    const filteredProducts = products.filter(
        product => (filter === 'tous' || product.category === filter) && product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const addToCart = (product, quantity) => {
        setCart(prevCart => {
            const existingProductIndex = prevCart.findIndex(item => item.id === product.id);
            if (existingProductIndex >= 0) {
                const updatedCart = [...prevCart];
                updatedCart[existingProductIndex].quantity += quantity;
                return updatedCart;
            } else {
                return [...prevCart, { ...product, quantity }];
            }
        });
    };

    const removeFromCart = (productId) => {
        setCart(prevCart => prevCart.filter(item => item.id !== productId));
    };

    const handleProductClick = product => {
        setSelectedProduct(product);
    };

    const totalItemsInCart = cart.reduce((total, item) => total + item.quantity, 0);

    return (
        <Router>
            <div className="App">
                <div className="top-bar-first-line">
                    <div className="logo-line">
                        <img src="link_logo.png" alt="Logo Whilf Rede" />
                    </div>
                    <input
                        type="text"
                        className="search-bar"
                        placeholder="Rechercher un produit ...."
                        onChange={e => setSearchQuery(e.target.value)}
                    />
                    <Link to="/cart" className="cart-icon">
                        <i className="fas fa-shopping-cart"></i>
                        <span className="cart-count">{totalItemsInCart}</span>
                    </Link>
                </div>

                <div className="separator"></div>

                <div className="top-bar-second-line">
                    <div className="filter-container">
                        {['tous', 'traditionnel', 'vegan', 'dessert'].map(category => (
                            <button
                                key={category}
                                className={`filter-button ${filter === category ? 'active' : ''}`}
                                onClick={() => setFilter(category)}
                            >
                                {category.charAt(0).toUpperCase() + category.slice(1)}
                            </button>
                        ))}
                    </div>
                </div>

                <Routes>
                    <Route
                        path="/"
                        element={
                            <div className="content">
                                {filteredProducts.map(product => (
                                    <Product key={product.id} product={product} onProductClick={handleProductClick} />
                                ))}
                            </div>
                        }
                    />
                                    <Routes>
                    {/* Redirige la racine (/) vers /menu */}
                    <Route path="/" element={<Navigate to="/menu" />} />
                    <Route path="/menu" element={<ProductList />} />
                    <Route path="/cart" element={<CartPage />} />
                    <Route path="/delivery" element={<DeliveryPage />} />
                </Routes>
                    <Route path="/cart" element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />} />
                    <Route path="/delivery" element={<DeliveryPage />} />
                </Routes>

                {selectedProduct && (
                    <Modal
                        product={selectedProduct}
                        onClose={() => setSelectedProduct(null)}
                        onAddToCart={addToCart}
                    />
                )}
            </div>
        </Router>
    );
}

export default App;
