import React, { useState } from 'react';
import './delivery.css';

const DeliveryPage = () => {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [city, setCity] = useState('');
  const [neighborhood, setNeighborhood] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!name || !surname || !city || !neighborhood || !phoneNumber) {
      setErrorMessage('Please fill in all fields.');
      return;
    }

    // Logique pour envoyer les données au backend ou les traiter
    console.log({
      name,
      surname,
      city,
      neighborhood,
      phoneNumber,
    });

    // Réinitialiser le formulaire après soumission (si nécessaire)
    setName('');
    setSurname('');
    setCity('');
    setNeighborhood('');
    setPhoneNumber('');
    setErrorMessage('');
  };

  return (
    <div className="delivery-page">
      <h2>Enter your delivery information</h2>
      <form onSubmit={handleSubmit}>
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label>
          Surname:
          <input
            type="text"
            value={surname}
            onChange={(e) => setSurname(e.target.value)}
            required
          />
        </label>
        <label>
          City:
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </label>
        <label>
          Neighborhood:
          <input
            type="text"
            value={neighborhood}
            onChange={(e) => setNeighborhood(e.target.value)}
            required
          />
        </label>
        <label>
          Phone Number:
          <input
            type="text"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
          />
        </label>
        <div className="button-group">
          <button type="submit">Submit</button>
          <button
            type="button"
            className="cancel-button"
            onClick={() => {
              // Réinitialiser les champs
              setName('');
              setSurname('');
              setCity('');
              setNeighborhood('');
              setPhoneNumber('');
              setErrorMessage('');
            }}
          >
            Cancel
          </button>
        </div>
      </form>
      <div className="payment-info">
        {/* Vous pouvez afficher des informations de paiement ou autres ici */}
        <p>Your payment details will appear here once the form is submitted.</p>
      </div>
    </div>
  );
};

export default DeliveryPage;
