import React from 'react';
import '../menu.css';

function Modal({ product, onClose, onAddToCart }) {
    if (!product) return null;

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                {/* Display product image at the top */}
                <img src={product.imgSrc} alt={product.name} className="modal-img" />

                {/* Product details */}
                <h2>{product.name}</h2>
                <p>Description: {product.description || "Aucune description disponible"}</p>
                <p>Prix: {product.price} â‚¬</p>

                {/* Buttons for "Add to Cart" and "Cancel" at the bottom */}
                <div className="modal-buttons">
                    <button className="add-to-cart" onClick={() => onAddToCart(product, 1)}>Ajouter au panier</button>
                    <button className="close-modal" onClick={onClose}>Annuler</button>
                </div>
            </div>
        </div>
    );
}

export default Modal;
